package operatori5cas;

public class Kupa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int r = 5;
	int H = 9;
	final float PI=3.14f;
	float V = 1/3+r*r*H*PI;
	
	System.out.println("Zapremina kupe prečnika r=" + r + " cm i visine H=" + H + " cm iznosi " + V + " cm kubnih.");
	}

}
