package operatori5cas;

public class Valjak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int r = 13;
	int H = 9;
	final float PI=3.14f;
	float A=2*r*(r+H)*PI;
	
	System.out.println("Površina valjda poluprečnika r="+r+"cm i visine H="
			+H+"cm iznosi A="+A+"cm kvadratnih.");
	
	}

}
