package pozdrav3cas;

public class KalkulatorOsnovni {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 12;
		int b = 7;
		
		System.out.println("Почетна вредност а=" + a);
		System.out.println("Почетна вредност b=" + b);
		System.out.println("Операције какулатора су следеће: ");
		System.out.println("\ta+b=" + (a+b));
		System.out.println("\ta-b=" + (a-b));
		System.out.println("\ta*b=" + (a*b));
		System.out.println("\tЦелобројно дељење a/b=" + (a/b));
		System.out.println("\tОстатак при дељењу a%b=" + (a%b));
	}

}
