package pozdrav3cas;

public class ZamenaMesta {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 6;
		System.out.print("\n\tПочетна вредност а = ");
		System.out.println(a);
		System.out.print("Почетна вредност b = ");
		System.out.println(b);
		int p;
		p = a;
		a = b;
		b = p;
		System.out.print("\nНова вредност а = ");
		System.out.println(a);
		System.out.print("Нова вредност b = ");
		System.out.println(b);
	}

}
