package promenljive4cas;

public class KrugObimPovrsina {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	double r = 3;
	double P;
	double O;
	final double PI = 3.14;
	O = 2*r*PI;
	P = r*r*PI;
	System.out.println("Obim kruga poluprečnika r=" + r + " cm je O=" + O + " cm.");
	System.out.println("Površina kruga poluprečnika r=" + r + " cm je P=" + P + " cm kvadratnih.");
	}

}
