package promenljive4cas;

public class Promenljive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 3;
		int b;
		System.out.print("Vrednost promenljive a je: ");
		System.out.println(a);
		b = a + 12;
		System.out.print("Vrednost promenljive b=a+12 je: ");
		System.out.println(b);
		a = a + 2;
		System.out.print("Vrednost promenljive a=a+2 je: ");
		System.out.println(a);
		int c = a + b;
		System.out.print("Zbir promenljivih a i b je: ");
		System.out.println(c);
	}

}
